import React, { useEffect, useRef, useState } from 'react'
import Nav from '../Nav/Nav';
import axios from "axios";
import Vehicle from '../Vehicle/Vehicle';
import {useReactToPrint} from "react-to-print";

const URL = "http://localhost:5000/vehicles";

const fetchHandler = async () =>{
  return await axios.get(URL).then((res) => res.data);
}
function Vehicles() {

  const [vehicles, setVehicles] = useState();
  useEffect(()=> {
    fetchHandler().then((data) => setVehicles(data.vehicles));
  },[])

  const ComponentsRef = useRef();
  const handlePrint = useReactToPrint({
    content: () => ComponentsRef.current,
    DocumentTitle:"Vehicle Details Report",
    onafterprint:()=>alert("Vehicles Report Successfully Download !"),
  })

  return (
    <div>
      <Nav/>
      <h1>Vehicle Details Display Page</h1>
      <div ref={ComponentsRef}>
        {vehicles && vehicles.map((vehicle, i) => (
          <div key={i}>
            <Vehicle vehicle={vehicle}/>
          </div>
        ))}
      </div>
      <button onClick={handlePrint}>Download Report</button>
    </div>
  )
}

export default Vehicles
